mapper.insert:
insert 
  into board
  	 (
  	   board_no
  	 , BOARD_TITLE
  	 ,BOARD_WRITER
  	 ,BOARD_CONTENT
  	 ,ORIGIN_NAME
  	 ,CHANGE_NAME
  	 )
values
	 (
	   seq_bno.nextval
	 , #{boardTitle}
	 , #{boardWriter}
	 , #{boardContent}
	 , #{originName}
	 , #{changeName}
	 )

mapper.update:
update
      board
   set board_title = #{boardTitle}
        ,board_content = #{boardContent}
        ,origin_name = #{originName}
        ,change_name = #{changeName}
 where board_no = #{boardNo}
